<script setup>
import { ref, onMounted, onUnmounted } from 'vue'

const maximized = ref(false)

onMounted(() => {
    // Listens for maximized events from main processes
    electron.ipcRenderer.on('maximized', (_event, isMaximized) => {
        maximized.value = isMaximized
    })
})

// Clear the listener
onUnmounted(() => {
    electron.ipcRenderer.removeAllListeners('maximized')
})

function close() {
    electron.ipcRenderer.invoke('close');
}

function maximize() {
    electron.ipcRenderer.invoke('maximize')
}

function restore() {
    electron.ipcRenderer.invoke('restore')
}

function minimize() {
    electron.ipcRenderer.invoke('minimize')
